        const base = Array.from({ length: 8 }, (_, i) => (character.skillSlots?.[i] ?? ''));
        const setsRaw = Array.isArray(character.skillSets) ? character.skillSets : null;

        const make8 = (arr, fallback) => Array.from({ length: 8 }, (_, i) => (arr?.[i] ?? fallback?.[i] ?? ''));

