                setConsoleLogs(prev => [...prev, 'âœ— ç”¨æ³•ï¼š']);
                setConsoleLogs(prev => [...prev, '   add gold <æ•°é‡>']);
                setConsoleLogs(prev => [...prev, '   add equip <è£…å¤‡ID>,<ç­‰çº§>ï¼ˆç­‰çº§å¯é€‰ï¼‰']);
